						--manages (employee-name, manager-name)--a) Find the names of all employees who work for First Bank Corporation.	SELECT e.employee_name FROM tbl_Employee e	JOIN tbl_Works w on e.employee_name = w.employee_name	WHERE company_name = 'First Bank Corporation';--(b) Find the names and cities of residence of all employees who work for Small Bank Corporation	SELECT e.employee_name, e.city FROM tbl_Employee e	JOIN tbl_Works w on e.employee_name = w.employee_name	WHERE company_name = 'Small Bank Corporation';--(c) Find the names, street addresses, and cities of residence of all employees who work for Small Bank Corporation and earn more than $10,000.
	SELECT e.employee_name,e.street,e.city FROM tbl_Employee e	JOIN tbl_Works w on e.employee_name = w.employee_name	WHERE company_name = 'Small Bank Corporation' AND salary > 10000;--(d) Find all employees in the database who live in the same cities as the companies for which they work.	SELECT e.employee_name,e.city FROM tbl_Employee e	JOIN tbl_Works w on e.employee_name = w.employee_name	JOIN tbl_Company c on w.company_name = c.company_name	WHERE e.city = c.city;--(e) Find all employees in the database who live in the same cities and on the same streets as do their managers.		SELECT e.employee_name, e.city, e.street FROM tbl_Employee e	JOIN tbl_Manages mm on e.employee_name = mm.employee_name	JOIN tbl_Employee m on m.employee_name = mm.manager_name	WHERE e.city = m.city AND e.street = m.street;	